import React from "react";
 const categories =["eggs,fish & meat", "buy more & save more","deal of this week","combo store"];


 const Categories=() => {
        return(
            <div style={styles.container}>
                {category.map((category,index) => (
                    <button key={index}style={styles.button}>
                        {category}
                    </button>
                ))}
            </div>
        );
 };


 const styles ={
    container:{display:"flex",gap:"10px",overflowX:"auto",padding:"10px"},
    button:{padding:"10px 20px",background:"#f5f5f5",border:"1px solid #ccc",borderRadius:"5px",cursor:"pointer"},

 };


 export default Categories
