import React from "react";
import { FaShoppingCart } from "react-icons/fa";
import logoImg from "../assets/logos.jpg"
import DropdownMenu from"./DropdownMenu";

const Header = () => {
    return(
        <header style={styles.header}>
            {/*logo*/}
            <div style={styles.logo}>
                <img src={logoImg} alt="" style={styles.logoImg} />

            </div>
            {/*searchbar*/}
            <input type="text" placeholder="Search for Products..." style={styles.searchBar}/>
            {/*navbar */}
            <nav style={styles.nav}>
                <a href="/">Shop by Category</a>
                <div style={{position:"relative"}}>
                   <DropdownMenu/>
                   </div>
                
               
                
                <button style={styles.login}>Login /Sign Up</button>
                <FaShoppingCart size={20} color="black" style={{marginLeft:"auto"}}/>

            </nav>
        </header>
    );
};
 const styles ={
    header:{
        display:"flex",
        justifyContent:"space-between",
        alignItems:"center",
        padding:"10px",
        background:"#E6EE9C",
        height:"170px",
    },
    logoImg:{
        width:"105px",
        position:"relative",
        left:"16px",
        bottom:"3px",
    },
    searchBar:{
        margin:"0,10px",
        padding:"10px",
        border:"1px solid #ccc", 
        borderRadius:"7px",
        left:"24px",
        position:"relative",
        height:"33px",
        width:"620px",
    },
    nav:{
        display:"flex",
        gap:"15px",
        alignItems:"center",
        textDecoration:"none",
    },
    login:{
        background:"#000",
        color:"#fff",
        padding:"5px 10px",
        borderRadius:"5px",
    },
   dropdownContainer:{
    position:"absolute",
    top:"30px",
    left:"0",
    zIndex:"1000",
    width:"300px",
   },


 };




export default Header;
