import React,{useState} from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination } from "swiper";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";




//image import
import img1 from "../assets/img1.webp";
import img2 from "../assets/img2.webp";
import img3 from "../assets/img3.webp";
import img4 from "../assets/img4.webp";


const imageSets=[
    [img1,img2,img3,img4],

]

const ImageSlider = () => {
    const [currentSetIndex, setCurrentSetIndex] = useState(0); // Tracks current image set
  
    // Function to switch to the next image set
    const handleNextSet = () => {
      setCurrentSetIndex((prevIndex) => (prevIndex + 1) % imageSets.length);
    };
  
    // Function to switch to the previous image set
    const handlePrevSet = () => {
      setCurrentSetIndex((prevIndex) =>
        prevIndex === 0 ? imageSets.length - 1 : prevIndex - 1
      );
    };
  
    return (
      <div>
        <h2 style={{ textAlign: "center" }}>Dynamic Slider</h2>
  
        {/* Swiper for the current image set */}
        <Swiper
          modules={[Navigation, Pagination]}
          spaceBetween={20}
          slidesPerView={4}
          navigation
          pagination={{ clickable: true }}
          style={{ padding: "20px" }}
        >
          {imageSets[currentSetIndex].map((image, index) => (
            <SwiperSlide key={index}>
              <div style={{ textAlign: "center" }}>
                <img
                  src={image}
                  alt={`Slide ${index}`}
                  style={{
                    width: "100%",
                    height: "200px",
                    objectFit: "cover",
                    borderRadius: "10px",
                  }}
                />
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
  
        {/* Buttons to change image sets */}
        <div style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}>
          <button
            onClick={handlePrevSet}
            style={{
              backgroundColor: "#1B5E20",
              color: "white",
              border: "none",
              padding: "10px 20px",
              marginRight: "10px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Previous Set
          </button>
          <button
            onClick={handleNextSet}
            style={{
              backgroundColor: "#1B5E20",
              color: "white",
              border: "none",
              padding: "10px 20px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Next Set
          </button>
        </div>
      </div>
    );
  };
  
  export default ImageSlider;
