import React,{useState} from "react";
import veg3 from "../assets/veg3.webp";
import fru2 from "../assets/fru2.webp";
import m2 from "../assets/m2.webp";
import gro2 from "../assets/gro2.webp";
import bev3 from "../assets/bev3.webp";


const productData = [
  { id: 1, name: "Onions (5kgs)", discount: "5%", price: 310, image: veg3 },
  { id: 2, name: "Kiwi (1kg)", discount: "5%", price: 250, image: fru2 },
  { id: 3, name: "Chicken (1kg)", discount: "15%", price: 310, image: m2 },
  { id: 4, name: "Toor Dal (2kgs)", discount: "10%", price: 200, image: gro2 },
  { id: 5, name: "TATA Coffee (1kg)", discount: "15%", price: 290, image: bev3 },
];

const Product = () => {
    const[cart,setCart]=useState({});
    // Function to handle "Add to Cart" clicks
  const handleAddToCart = (product) => {
    setCart((prevCart)=>({
       ...prevCart,
        [product.id]:{
            ...product,
            quantity:(prevCart[product.id]?.quantity||0)+2
        },
    }));
};

//function to increase quantity
        const handleIncreaseQuantity =(productId) =>{
            setCart((prevCart) => ({
                ...prevCart,
                [productId]:{
                    ...prevCart[productId],
                    quantity:prevCart[productId].quantity + 1 ,
                },
            }));
        };
        // Function to decrease quantity
  const handleDecreaseQuantity = (productId) => {
    setCart((prevCart) => {
      const newCart = { ...prevCart };
      if (newCart[productId].quantity > 1) {
        newCart[productId].quantity -= 1;
      } else {
        delete newCart[productId];
      }
      return newCart;
    });
  };

  return (
    <div>
    <div style={styles.productContainer}>
      {productData.map((product) => (
        <div key={product.id} style={styles.card}>
          <img src={product.image} alt={product.name} style={styles.image} />
          <h3>{product.name}</h3>
          <p>Discount: {product.discount}</p>
          <p>Price: &#x20B9;{product.price}</p>
          {cart[product.id] ?(
            <div style={styles.quantityControls}>
                  
          <button style={styles.quantityButton}onClick={()=>handleDecreaseQuantity(product.id)}></button>
          <span style={styles.quantityDisplay}>
            {cart[product.id].quantity}
          </span>
          <button style={styles.quantityButton}onClick={()=>handleIncreaseQuantity(product)}>+</button>
          
        </div>
      ):(
        <button style={styles.button}
        onClick={()=>handleAddToCart(product)}>
            Add to Cart
        </button>
      )}
    </div>
      ))}
      </div>
      <div style={styles.cartContainer}>
        <h2>Your Cart</h2>
        {Object.keys(cart).length === 0 ? (
          <p>Cart is empty</p>
        ) : (
          Object.values(cart).map((item) => (
            <div key={item.id} style={styles.cartItem}>
              <span>{item.name}</span>
              <span>
                {item.quantity} x &#x20B9;{item.price} = &#x20B9;
                {item.quantity * item.price}
              </span>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

  

const styles = {
  productContainer: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
    gap: "20px",
    padding: "20px",
    position:"relative",
    left:"103px",
  },
  card: {
    border: "1px solid #ccc",
    borderRadius: "5px",
    padding: "10px",
    textAlign: "center",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    backgroundColor:"white",
  },

  
  image: {
    width: "75%",
    height: "150px",
    objectFit: "cover",
    marginBottom: "10px",
  },
  button: {
    backgroundColor: "#1B5E20",
    color: "white",
    border: "none",
    borderRadius: "5px",
    padding: "10px",
    cursor: "pointer",
  },
  quantityControls: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "10px",
  },
  quantityButton: {
    backgroundColor: "#1B5E20",
    color: "white",
    border: "none",
    borderRadius: "5px",
    padding: "5px 10px",
    cursor: "pointer",
  },
  quantityDisplay: {
    fontWeight: "bold",
    fontSize: "16px",
  },
  cartContainer: {
    marginTop: "20px",
    padding: "20px",
    backgroundColor: "#f9f9f9",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    padding: "10px 0",
    borderBottom: "1px solid #ddd",
  },

};

export default Product;
